import React from 'react';
import { useTimer } from "C:/Users/bar1s/Desktop/react/auction/node_modules/reactjs-countdown-hook/dist/useTimer.js";

const Product = (props) => {
    const { auction } = props;
    const { title, image, startPrice, explanation, endDate, startDate } = auction;


    var secondDate = new Date(endDate);

    const {
        isActive,
        counter,
        seconds,
        minutes,
        hours,
        days,
        pause,
        resume,
        reset,
    } = useTimer( secondDate, handleTimerFinish);

    const handleTimerFinish = () => {
        document.getElementById("demo").value = "Time is up!";
    }

    var now = new Date().getTime();
    //let days, hours, minutes, seconds;

    setInterval(function () {

        var distance = secondDate - now;
        days = Math.floor(distance / (1000 * 60 * 60 * 24));
        hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
        minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
        seconds = Math.floor((distance % (1000 * 60)) / 1000);
    }, 1000);

    return (
        <div className="col mb-5">
            <div className="card h-100">
                <img className="card-img-top" src={image || "https://dummyimage.com/450x300/dee2e6/6c757d.jpg"} alt="..." />
                <div className="card-body p-5">
                    <div className="text-center">
                        <h5 className="fw-bolder">{title}</h5>
                        <p>{explanation}</p>
                        Price : ${startPrice} <br></br>

                        <div id='time'>{`${days} : ${hours} : ${minutes} : ${seconds}`}</div>

                    </div>
                </div>
                <div className="card-footer p-4 pt-0 border-top-0 bg-transparent">
                    <div className="text-center">
                        <button className="btn btn-outline-dark mt-auto">View Details</button>
                    </div>
                </div>
            </div>
        </div>

    );
};

export default Product;
