import React,{useState} from 'react';
import Input from '../Components/Input';


const SignUpPage = () => {

    const [form, setForm] = useState({
        name: null,
        surname: null,
        username: null,
        email: null,
        password: null,
        passwordRepeat: null
    })
    const [errors, setError] = useState({});

    const onChange = (e) => {
        console.log(e.target.name +" + " +e.target.value);
     }

    const onClickSignup = async (e) => {
        e.preventDefault();
        const { name, surname, username, email, password } = form;
        const body={
            name,
            surname,
            username,
            email,
            password
        }
     }

    const{ usernameError, passwordError , nameError , surnameError , emailError } = errors;

    let passwordRepeatError;
    if (form.password !== form.passwordRepeat) {
      passwordRepeatError = 'Password mismatch';
    }
  
  
    return (
        <div className='container'>
            <form>
                <h1  className='text-center'>Sign Up</h1>
                <Input name='username' label='Name' error={nameError} onChange={onChange} />

                <Input name='surname' label='Surname' error={surnameError} onChange={onChange} />

                <Input name='username' label='Username' error={usernameError} onChange={onChange} />

                <Input name='email' label='Email' error={emailError} onChange={onChange} type='email' />

                <Input name='password' label='Password' error={passwordError} onChange={onChange} type='password' />

                <Input name='passwordRepeat' label='Password Repeat' error={passwordRepeatError} onChange={onChange} type='password' />
            </form>
        </div>
    );
};

export default SignUpPage;
