import React from 'react';
import { myWonAuctions } from '../Api/ApiCalls';
import { useSelector } from 'react-redux';


const MyBids = () => {

    const { loggedInUsername } = useSelector(store => ({ loggedInUsername: store.username }));
    const [loadFailure, setLoadFailure] = useState(false);
    const [auction, setAuction] = useState({});

    useEffect(() => {
      loadAuctions();
    }, []);
  
    const loadAuctions = ()  => {
      try {
        const response =  myWonAuctions(loggedInUsername);
        setAuction(response.data);
      } catch (error) {
        setLoadFailure(true);
      }
    };


    return (
        <div>



            <div className="container px-4 px-lg-5 mt-5">
                <div className="row gx-4 gx-lg-5 row-cols-sm-1  row-cols-md-3 row-cols-xl-3 justify-content-center">

                    {auction.map(auction => (
                        <Product key={auction.id} auction={auction} />
                    ))}

                    {loadFailure && <div className='text-center text-danger'> Failed to load auctions </div>}


                </div>
                {actionDiv}
            </div>


        </div>
    );
};

export default MyBids;
