import React from 'react';
import Input from './Input';
import { useEffect, useState } from 'react';
import ButtonWithProgress from './ButtonWithProgress';
import { postIdImage, putIdentity } from '../Api/ApiCalls';
import { useSelector } from 'react-redux';

const VerifyAccount = () => {

    const [error, setError] = useState();
    const [newImage, setNewImage] = useState();
    const [idNumber, setIdNumber] = useState();
    const [imageId, setImageId] = useState();
    const { username: loggedInUsername } = useSelector(store => ({ username: store.username }));
    const numberRegex = /^[0-9]+$/;


     useEffect(() => {
        setError(undefined)
    }, [newImage,idNumber]);

    const onChangeFile = (event) => {
        if (event.target.files.length < 1) {
            return;
        }
        const file = event.target.files[0];
        const fileReader = new FileReader();
        fileReader.onload = () => {
            setNewImage(fileReader.result);
            uploadFile(file);
        }
        fileReader.readAsDataURL(file);
    }

    const uploadFile = async file => {
        const attachment = new FormData();
        attachment.append('file', file);
        const response = await postIdImage(attachment);
        console.log("response", response)
        setImageId(response.data.id);
    };


    const saveId = async () => {
        if(!idNumber.match(numberRegex)){
            setError("Id number must be numeric");
            return;
        }

        const body = {
            idNumber,
            imageId,
            loggedInUsername
        }
        try {
            console.log(body)
            const response = await putIdentity(body)
            console.log("response", response)
            setError(undefined)
        } catch (error) {
            setError(error.response.data);
        }

    }

    const onChangeIdNumber = (event) => {
     setIdNumber(event.target.value);
     setError(undefined) 
    }
    return (
        <div className='container card' style={{ background: '#f3f5f5' }}>
            <h2 className='text-center card-header'>ID Verification</h2>
            <div className='card-body'>

                <img className='img-thumbnail' width="400" height="400" src={newImage} alt='...' />

                <Input label='Add ID photo' type="file" onChange={onChangeFile}  />
                <hr />

                <Input label='ID number' type="text" onChange={onChangeIdNumber}/>

                {error && <div className="alert alert-danger" role="alert">
                    {error}
                </div>}
                <ButtonWithProgress onClick={saveId} text='Upload ID' />
            </div>
        </div>
    );
};

export default VerifyAccount;
