import React, { useEffect } from 'react';
import './Auction.css'
import Input from '../Components/Input';
import { useParams } from 'react-router-dom';
import { getAuction } from '../Api/ApiCalls';
import { useState } from 'react';
import { Link } from 'react-router-dom';
import { useCountdown } from '../Components/Countdown';
import ButtonWithProgress from '../Components/ButtonWithProgress';
import { useSelector } from 'react-redux';

/* 
auction yoksa error sayfası göster
teklif kontrol et 
otomatik teklif verme ekle
mesaj ekle ...
 */
const Auction = (props) => {
    const [auction, setAuction] = useState({
        bids: [],
        creator: {},
        buyer: null,
        category: null,
        endDate: null,
        explanation: null,
        fileAttachment: null,
        sellNowPrice: null,
        startDate: null,
        startPrice: null,
        title: null
    });
    const [notFound, setNotFound] = useState(false);
    const params = useParams();
    let { id } = params;

    const isLoggedIn =  useSelector (store => store.isLoggedIn);

    useEffect(() => {
        loadAuction(id);
        setNotFound(false);
    }, [id]);

    const loadAuction = async (id) => {
        try {
            const response = await getAuction(id);
            setAuction(response.data);
        } catch (error) {
            setNotFound(true);
        }
    }

    const { bids, buyer, category, endDate, explanation, fileAttachment, sellNowPrice, startDate, startPrice, title } = auction;
    const { creator } = auction;
    const { name, username } = creator;

    const [days, hours, minutes, seconds] = useCountdown(endDate);




    const onClickBid = () => {
    }

    console.log(loggedInUsername)

    let showTime = "";
    let html = "";
    if (days + hours + minutes + seconds <= 0) {
        html = (<span className="text-danger">"Auction is over"</span>);
    } else {
        showTime = `${days} days ${hours} hours ${minutes} minutes ${seconds} seconds`;
        html = (<>
            <span className="text-danger">{showTime}</span>

            {isLoggedIn&& <div className="action my-2">
                <Input label='Enter your bid' name='bid' type="text" />
                <ButtonWithProgress className="add-to-cart btn btn-default
            " text='bid on product' onClick={onClickBid} ></ButtonWithProgress>
            </div>}
            {!isLoggedIn &&  <Link to="/login" className="action my-2">Login to bid</Link>}

        </>
        )
    }

    return (
        <div className="container">
            <div className="card mt">
                <div className="container-fliud my-5">
                    {!notFound && (<div className="wrapper row">

                        <div id="carouselExampleControls" className="carousel slide preview col-md-6" data-ride="carousel">
                            <div className="carousel-inner">


                                {fileAttachment && (
                                    <div>
                                        {fileAttachment.fileType.startsWith('image') && (
                                            <div className="carousel-item active">
                                                <img src={'images/attachments/' + fileAttachment.name}
                                                    className="d-block w-100" alt={fileAttachment.name} />
                                            </div>
                                        )}
                                        {!fileAttachment.fileType.startsWith('image')
                                            && <strong>Unknown attachment</strong>}
                                    </div>
                                )}





                                <div className="carousel-item">
                                    <img src="http://placekitten.com/400/252" className="d-block w-100" alt="..." />
                                </div>
                                <div className="carousel-item">
                                    <img src="http://placekitten.com/400/252" className="d-block w-100" alt="..." />
                                </div>
                            </div>
                            <a className="carousel-control-prev" href="#carouselExampleControls" role="button" data-slide="prev">
                                <span className="carousel-control-prev-icon" aria-hidden="true"></span>
                                <span className="sr-only">Previous</span>
                            </a>
                            <a className="carousel-control-next" href="#carouselExampleControls" role="button" data-slide="next">
                                <span className="carousel-control-next-icon" aria-hidden="true"></span>
                                <span className="sr-only">Next</span>
                            </a>
                        </div>

                        <div className="details col-md-6 my-2">
                            <h3 className="product-title">{title}</h3>
                            <h5 > Seller : <Link to={`/user/${username}`}> {name} </Link> </h5>
                            <div><hr></hr></div>


                            <h5>Description</h5>
                            <p className="product-description">{explanation}</p>

                            <div><hr></hr></div>

                            <h4 >Current Price: <span className="text-success">${Number(startPrice).toLocaleString()}</span></h4>

                            {html}



                        </div>
                    </div>)}
                    
                    {notFound && (<div className="alert alert-danger" role="alert">
                        <strong>Auction not found</strong>
                    </div>)}

                </div>
            </div>
        </div>
    );
};

export default Auction;
