import React, { useState, useEffect } from 'react';
import ProfileImageWithDefault from './ProfileImageWithDefault';
import Input from './Input';
import { updateUser } from '../Api/ApiCalls';
import ButtonWithProgress from './ButtonWithProgress';
import { useApiProgress } from '../Shared/ApiProgress';
import { useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';

const ProfileCard = props => {
  const [inEditMode, setInEditMode] = useState(false);
  const [updatedName, setUpdatedName] = useState();
  const [user, setUser] = useState({});
  const { username: loggedInUsername } = useSelector(store => ({ username: store.username }));
  const routeParams = useParams();
  const pathUsername = routeParams.username;
  const [editable, setEditable] = useState(false);
  const [newImage, setNewImage] = useState();


  useEffect(() => {
    setUser(props.user);
  }, [props.user]);

  useEffect(() => {
    setEditable(pathUsername === loggedInUsername)
  }, [pathUsername, loggedInUsername]);

  const { username, name, image } = user;

  useEffect(() => {
    if (!inEditMode) {
      setUpdatedName(undefined); // if yo are not in edit mode, then reset the name
      setNewImage(undefined); // if yo are not in edit mode, then reset the new image
    } else {
      setUpdatedName(name);
    }
  }, [inEditMode, name]);

  const onClickSave = async () => {
    const body = {
      name: updatedName,
      image: newImage
    };
    try {
      const response = await updateUser(username, body);
      setInEditMode(false);
      setUser(response.data);
    } catch (error) { }
  };


  const onChangeFile = event => {
    const file = event.target.files[0];
    const fileReader = new FileReader();  //read the file
    fileReader.onloadend = () => { //when the file is loaded
      setNewImage(fileReader.result); //set the new image
    };
    fileReader.readAsDataURL(file);
  };


  const pendingApiCall = useApiProgress('put', '/users/' + username);


  return (
    <div className="card text-center">
      <div className="card-header">
        <ProfileImageWithDefault className="rounded-circle shadow"
          width="200" height="200" alt={`${username} profile`}
          image={image} tempimage={newImage}

        />
      </div>
      <div className="card-body">
        {!inEditMode && (
          <>
            <h3>
              {name}-{username}
            </h3>
            {editable && (<button className="btn btn-success d-inline-flex" onClick={() => setInEditMode(true)}>
              <i className="material-icons">edit</i>
              Edit
            </button>)}
          </>
        )}
        {inEditMode && (
          <div>
            <Input
              label='Change Display Name'
              defaultValue={name}
              onChange={event => {
                setUpdatedName(event.target.value);
              }}
            />
            <input type="file" onChange={onChangeFile} />

            <div>
              <ButtonWithProgress
                className="btn btn-primary d-inline-flex"
                onClick={onClickSave}
                disabled={pendingApiCall}
                pendingApiCall={pendingApiCall}
                text={
                  <>
                    <i className="material-icons">save</i>
                    Save
                  </>
                }
              />
              <button className="btn btn-light d-inline-flex ml-1" onClick={() => setInEditMode(false)} disabled={pendingApiCall}>
                <i className="material-icons">close</i>
                Cancel
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ProfileCard;
