import React from 'react';
import Input from './Input';
import { useEffect, useState } from 'react';
import ButtonWithProgress from './ButtonWithProgress';
import { postIdImage } from '../Api/ApiCalls';

const VerifyAccount = () => {

    const [validationErrors, setValidationErrors] = useState({});
    const [newImage, setNewImage] = useState();
    const [idNumber, setIdNumber] = useState();
    const [ imageId , setImageId ] = useState();

    // useEffect(() => {
    //     setValidationErrors(previousValidationErrors => ({
    //         ...previousValidationErrors,
    //     }));
    // }, [newImage]);

    const onChangeFile = (event) => {
        if (event.target.files.length < 1) {
            return;
        }
        const file = event.target.files[0];
        const fileReader = new FileReader();
        fileReader.onload = () => {
            setNewImage(fileReader.result);
            uploadFile(file);
        }
        fileReader.readAsDataURL(file);
    }

    const uploadFile = async file => {
        const attachment = new FormData();
        attachment.append('file', file);
        const response = await postIdImage(attachment);
        console.log("response",response)
        setImageId(response.data.id);
    };


    const saveId = () => {
        const body={
            idNumber,
            imageId
        }

    }

    const { image: imageError, idNumber: idNumberError } = validationErrors;

    return (
        <div className='container card' style={{ background: '#f3f5f5' }}>
            <h2 className='text-center card-header'>ID Verification</h2>
            <div className='card-body'>

                <img className='img-thumbnail' width="400" height="400" src={newImage} alt='...' />

                <Input label='Add ID photo' type="file" onChange={onChangeFile} error={imageError} />
                <hr />

                <Input label='ID number' type="text" onChange={event => { setIdNumber(event.target.value) }} error={idNumberError} />

                <ButtonWithProgress onClick={saveId} text='Upload ID' />
            </div>
        </div>
    );
};

export default VerifyAccount;
