import React from 'react';


const ProfileImageWithDefault = (props) => {
    
    const defaultPicture= 'https://www.pngall.com/wp-content/uploads/5/Profile-PNG-File.png';

    const { image } =props;

    let imageSource = defaultPicture;

    if(image){ //artık farklı dosyalardalar o yüzden profile a yazılıyor
        imageSource = 'images/profile/'+image;
    }

    return (
            <img  alt={`profile`}
             src={ imageSource} {...props} // 
             onError={event => { event.target.src=defaultPicture}} />
    );
};

export default ProfileImageWithDefault;
