        pattern="/^[a-zA-Z0-9.!#$%&’*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/"
    }

    return (
        <div className='form-group'>
        <label>{label}</label>
        <input className={className}
            name={name}
            onChange={onChange}
            type={type}
