import React from 'react';


const Product = (props) => {
    const { auction } = props;
    const { title, image, startPrice, explanation, endDate, startDate } = auction;

   

    var secondDate =  new Date(endDate);

    // Update the count down every 1 second
    var x = setInterval(function () {
        var now = new Date().getTime();

        var distance = secondDate - now;

        // Time calculations for days, hours, minutes and seconds
        var days = Math.floor(distance / (1000 * 60 * 60 * 24));
        var hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
        var minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
        var seconds = Math.floor((distance % (1000 * 60)) / 1000);

        document.getElementById("demo").innerHTML = days + "d " + hours + "h "
            + minutes + "m " + seconds + "s ";

        // If the count down is over, write some text 
        if (distance < 0) {
            clearInterval(x);
            document.getElementById("demo").innerHTML = "TIME EXPIRED";
        }
    }, 1000);

    return (
        <div className="col mb-5">
            <div className="card h-100">
                <img className="card-img-top" src={image || "https://dummyimage.com/450x300/dee2e6/6c757d.jpg"} alt="..." />
                <div className="card-body p-5">
                    <div className="text-center">
                        <h5 className="fw-bolder">{title}</h5>
                        <p>{explanation}</p>
                        Price : ${startPrice} <br></br>
                        
                        <p id='demo'></p>
                        
                    </div>
                </div>
                <div className="card-footer p-4 pt-0 border-top-0 bg-transparent">
                    <div className="text-center">
                        <button className="btn btn-outline-dark mt-auto">View Details</button>
                    </div>
                </div>
            </div>
        </div>

    );
};

export default Product;
