import React, { useEffect, useState } from 'react';
import { getByIdIdentity } from '../Api/ApiCalls';
import { useParams } from 'react-router-dom';
import ButtonWithProgress from './ButtonWithProgress';

const UserAuth = () => {

    const { id } = useParams();
    const[identity, setIdentity] = useState();

    useEffect(() => {
        loadPage();
        setIdentity(undefined);
    }, []);


    const loadPage = async () => {
        try {
            const response = await getByIdIdentity(id);
            console.log(response.data);
            setIdentity(response.data);
        }
        catch (error) {
            console.log(error.response.data);
        }
    }

    const onClickConfirm =  () => {
    
    }

    //'images/identity/
    return (
        <div className='container card' style={{ background: '#f3f5f5' }}>
            <h2 className='text-center card-header'>User ID Authentication</h2>
            <div className='card-body'>

                <img className='img-thumbnail' width="400" height="400" src={`images/identity/${identity.imageId}`} alt={identity.id} />


                <hr />

                <ButtonWithProgress onClick={onClickConfirm} text='Confirm User' />
                <ButtonWithProgress onClick={onClickConfirm} text='Reject' className='my-4 btn btn-danger' />
            </div>
        </div>
    );
};

export default UserAuth;
