import React, { useEffect } from 'react';
import Input from '../Components/Input';
import ButtonWithProgress from '../Components/ButtonWithProgress';
import { useState } from 'react';
import { createAuction } from '../Api/ApiCalls';
import { getAuction } from '../Api/ApiCalls';
import { postAuctionAttachment } from '../Api/ApiCalls';


const NewProduct = (props) => {
    const [newImage, setNewImage] = useState();
    const [errors, setError] = useState({});
    const [imageId, setImageId] = useState(); 
    const [form, setForm] = useState({
        title: null,
        startPrice: null,
        sellNowPrice: null,
        startDate: null,
        endDate: null,
        fileAttachment: null,
        explanation: null
    });


    const onChange = (event) => {
        const { name, value } = event.target;

        setError((previousError) => ({ ...previousError, [name]: undefined }));

        setForm((previousForm) => ({ ...previousForm, [name]: value }));
    }


    const onChangeFile = (event) => {
        if (event.target.files.length < 1) {
            return; 
        }
        const file = event.target.files[0];
        const fileReader = new FileReader();
        fileReader.onload = () => {
            setNewImage(fileReader.result);
            uploadFile(file);
        }
        fileReader.readAsDataURL(file);
    }
    const uploadFile = async file => {
        const attachment = new FormData();
        attachment.append('file', file);
        const response = await postAuctionAttachment(attachment);
        setImageId(response.data.id);//seçilen resmin id sini al
    };



    const onClickSave = async () => {
        const { title, startPrice, sellNowPrice, endDate } = form;
        let timeNow = new Date();
        let timeEnd = new Date(endDate);
        timeEnd = timeEnd.toISOString();
        let textarea = document.getElementById('explanation').value;
        const body = {
            title,
            startPrice,
            sellNowPrice,
            endDate: timeEnd.slice(0, 19),
            startDate: timeNow.toISOString().slice(0, 19),
            attachmentId: imageId,
            explanation: textarea
        };
        try {
            console.log("body", body);
            const response = await createAuction(body);

            props.history.push(`/auction/${response.data.id}`);
        } catch (error) {
            if (error.response.data.validationErrors) {
                setError(error.response.data.validationErrors);
            }
        }
    }

    return (
        <div className='container border border-secondary rounded py-2 my-5 '
            style={{ background: '#F7F7F9' }}>
            <div className='col-xl-9 col-lg-10 col-md-12 col-sm-12 mx-auto'>
                <h1 className='text-center'> Sell Your Product </h1>

                <img className='img-thumbnail'
                 width="250" height="250" src={newImage} alt='newImage' />

                <Input label='Add photo for product' type="file" onChange={onChangeFile} />
                <Input label='Title' name='title' onChange={onChange} />

                <select name='category' >
                    <option value="">Category</option>
                </select >

                <div className="form-group">
                    <label >Write an explanation</label>
                    <textarea className="form-control" id='explanation' ></textarea>
                </div>

                <Input label='Starting Price' name='startPrice' onChange={onChange} />
                <Input label='Sell Now Price' name='sellNowPrice' onChange={onChange} />
                <Input label='End Date' name='endDate' type='datetime-local' onChange={onChange} />

                <div className='text-center'>
                    <ButtonWithProgress text='Upload the Product' onClick={onClickSave}
                    />
                </div>
            </div>
        </div>
    );
};

export default NewProduct;
