import React, { useEffect } from 'react';
import './Auction.css'
import Input from '../Components/Input';
import { useParams } from 'react-router-dom';
import { getAuction } from '../Api/ApiCalls';
import { useState } from 'react';
import { Link } from 'react-router-dom';
import { useCountdown } from '../Components/Countdown';
import ButtonWithProgress from '../Components/ButtonWithProgress';

/* 
auction yoksa error sayfası göster
teklif kontrol et 
otomatik teklif verme ekle
mesaj ekle ...
 */
const Auction = (props) => {
    const [auction, setAuction] = useState({
        bids: [],
        creator: {},
        buyer: null,
        category: null,
        endDate: null,
        explanation: null,
        image: null,
        sellNowPrice: null,
        startDate: null,
        startPrice: null,
        title: null
    });
    const [notFound, setNotFound] = useState(false);
    const [isActive, setIsActive] = useState(false);
    const params = useParams();
    let { id } = params;


    const { bids, buyer, category, endDate, explanation, image, sellNowPrice, startDate, startPrice, title } = auction;
    const { creator } = auction;
    const { name, username } = creator;

    const [days, hours, minutes, seconds] = useCountdown(endDate);

    useEffect(() => {
        loadAuction(id);
    }, [id]);


    const loadAuction = async (id) => {
        try {
            const response = await getAuction(id);
            setAuction(response.data);
        } catch (error) {
            setNotFound(true);
        }
    }

    const onClickBid = () => {
    }

    let showTime = "";

    if (days + hours + minutes + seconds <= 0) {
        showTime = "Auction is over";
        setIsActive(false);
    } else {
        showTime = `${days} days ${hours} hours ${minutes} minutes ${seconds} seconds`;
    }

    return (
        <div className="container">
            <div className="card mt">
                <div className="container-fliud my-5">
                    <div className="wrapper row">
                        <div className="preview col-md-6">

                            <div className="preview-pic tab-content">
                                <div className="tab-pane active" id="pic-1"><img src="http://placekitten.com/400/252" /></div>
                                <div className="tab-pane" id="pic-2"><img src="http://placekitten.com/400/252" /></div>
                                <div className="tab-pane" id="pic-3"><img src="http://placekitten.com/400/252" /></div>
                                <div className="tab-pane" id="pic-4"><img src="http://placekitten.com/400/252" /></div>
                                <div className="tab-pane" id="pic-5"><img src="http://placekitten.com/400/252" /></div>
                            </div>
                            <ul className="preview-thumbnail nav nav-tabs">
                                <li className="active"><a data-target="#pic-1" data-toggle="tab"><img src="http://placekitten.com/200/126" /></a></li>
                                <li><a data-target="#pic-2" data-toggle="tab"><img src="http://placekitten.com/200/126" /></a></li>
                                <li><a data-target="#pic-3" data-toggle="tab"><img src="http://placekitten.com/200/126" /></a></li>
                                <li><a data-target="#pic-4" data-toggle="tab"><img src="http://placekitten.com/200/126" /></a></li>
                                <li><a data-target="#pic-5" data-toggle="tab"><img src="http://placekitten.com/200/126" /></a></li>
                            </ul>

                        </div>
                        <div className="details col-md-6 my-2">
                            <h3 className="product-title">{title}</h3>
                            <h5 > Seller : <Link to={`/user/${username}`}> {name} </Link> </h5>
                            <div><hr></hr></div>


                            <h5>Description</h5>
                            <p className="product-description">{explanation}</p>

                            <div><hr></hr></div>

                            <h4 >Current Price: <span className="text-success">${startPrice}</span></h4>
                            <span className="text-danger">{showTime}</span>

                            <div className="action my-2">
                                <Input label='Enter your bid' name='bid' type="text" />
                                <ButtonWithProgress className="add-to-cart btn btn-default
                                " text='bid on product' onClick={onClickBid} ></ButtonWithProgress>
                            </div>



                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Auction;
