import React from 'react';
import { useEffect, useState } from 'react';

const Product = (props) => {
    const { auction } = props;
    const { title, image, startPrice, explanation, endDate, startDate } = auction;



    const useCountdown = (targetDate) => {
        const countDownDate = new Date(targetDate).getTime();

        const [countDown, setCountDown] = useState(
            countDownDate - new Date().getTime()
        );

        useEffect(() => {
            const interval = setInterval(() => {
                setCountDown(countDownDate - new Date().getTime());
            }, 1000);

            return () => clearInterval(interval);
        }, [countDownDate]);

        return getReturnValues(countDown);
    };

    const getReturnValues = (countDown) => {
        // calculate time left
        const days = Math.floor(countDown / (1000 * 60 * 60 * 24));
        const hours = Math.floor(
            (countDown % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60)
        );
        const minutes = Math.floor((countDown % (1000 * 60 * 60)) / (1000 * 60));
        const seconds = Math.floor((countDown % (1000 * 60)) / 1000);

        return [days, hours, minutes, seconds];
    };
     let time = useCountdown(endDate);
    //console.log(time);
    const{ days, hours, minutes, seconds } = time;

    let showTime ="";

    if (days + hours + minutes + seconds <= 0) {
        showTime = "Auction is over";
    }else{
        showTime = `${days} days ${hours} hours ${minutes} minutes ${seconds} seconds`;

    }

  
    return (
        <div className="col mb-5">
            <div className="card h-100">
                <img className="card-img-top" src={image || "https://dummyimage.com/450x300/dee2e6/6c757d.jpg"} alt="..." />
                <div className="card-body p-5">
                    <div className="text-center">
                        <h5 className="fw-bolder">{title}</h5>
                        <p>{explanation}</p>
                        Price : ${startPrice} <br></br>
                        <span className="text-danger">{showTime}</span>
                       
                       

                    </div>
                </div>
                <div className="card-footer p-4 pt-0 border-top-0 bg-transparent">
                    <div className="text-center">
                        <button className="btn btn-outline-dark mt-auto">View Details</button>
                    </div>
                </div>
            </div>
        </div>

    );
};

export default Product;
