import React, { useEffect } from 'react';
import { getByIdIdentity } from '../Api/ApiCalls';
import { useParams } from 'react-router-dom';
import ButtonWithProgress from './ButtonWithProgress';

const UserAuth = () => {

    const { id } = useParams();

    useEffect(() => {
        loadPage();
    }, []);


    const loadPage = async () => {
        try {
            const response = await getByIdIdentity(id);
            console.log(response.data);
        }
        catch (error) {
            console.log(error.response.data);
        }
    }


    return (
        <div className='container card' style={{ background: '#f3f5f5' }}>
            <h2 className='text-center card-header'>ID Verification</h2>
            <div className='card-body'>

                <img className='img-thumbnail' width="400" height="400" src={newImage} alt='...' />

                <hr />

                <ButtonWithProgress onClick={onClickConfirm} text='Confirm User' />
                <ButtonWithProgress onClick={saveId} text='Reject' className='my-4 btn btn-danger' />
            </div>
        </div>
    );
};

export default UserAuth;
