import React, { useEffect , useState} from 'react';;
import { useSelector } from 'react-redux';
import Product from '../Components/Product';
import { getAuctions } from '../Api/ApiCalls';
import './homepage.css';
/*  API PROGRESSLER EKLE YUKLEME SAYFASI EKLE
*/

const HomePage = () => {

  const [page, setPage] = useState({
    content: [],
    size: 5,
    number: 0
  });

  const [loadFailure, setLoadFailure] = useState(false);


  useEffect(() => {
    loadAuctions();
  }, []);

  const loadAuctions = async page => {
    try {
      const response = await getAuctions(page);
      setPage(response.data);
    } catch (error) {
      setLoadFailure(true);
    }
  };

  const onClickNext = () => {
    const nextPage = page.number + 1;
    loadAuctions(nextPage);
  }
  const onClickPrevious = () => {
    const previousPage = page.number - 1;
    loadAuctions(previousPage);
  }

  return (
    <div className='body'>
      <header className="bg-dark py-4">
        <div className="container px-4 px-lg-5 my-4">
          <div className="text-center text-white">
            <h1 className="display-4 fw-bolder">Shop in style</h1>
            <p className="lead fw-normal text-white-50 mb-0">With this shop hompeage template</p>
          </div>
        </div>
      </header>

      <div className="container px-4 px-lg-5 mt-5">
        <div className="row gx-4 gx-lg-5 row-cols-sm-1  row-cols-md-3 row-cols-xl-3 justify-content-center">




        </div>
      </div>
      <footer id="footer" className="py-5 bg-dark bottom-0">
        <div className="container"><p className="m-0 text-center text-white">Copyright &copy; Barış Çevik 2022</p></div>
      </footer>

    </div>

  );
};

export default HomePage;
