import React, { useEffect, useState } from 'react';
import { getAllIdentity } from '../Api/ApiCalls';
import { Link } from 'react-router-dom';
import './verifyList.css';
import Modal from './Modal';
import ButtonWithProgress from './ButtonWithProgress';

const VerifyList = () => {

    const [identities, setIdentities] = useState([]);
    const [selectedIdentity, setSelectedIdentity] = useState(undefined);
    const [error, setError] = useState();
    const [modalVisible, setModalVisible] = useState(false);


    useEffect(() => {
        loadIdentities();
    }, []);

    const loadIdentities = async () => {
        try {
            const response = await getAllIdentity();
            setIdentities(response.data);
        }
        catch (error) {
            setError(error.response.data);
        }
    }

    const onClickDetail = (identity) => {
        setModalVisible(true);
        setSelectedIdentity(identity);
    }

    const onClickCancel = () => {
        setModalVisible(false);
        setSelectedIdentity(undefined)
    }

    const onClickVerify = () => {

    }

    const onClickDelete = () => {

    }


    return (
        <div className='card' id='scroll'>
            <h3 className='card-header'>Authentication Requests</h3>
            <div className='list-group-flush'>
                {identities.map(identity => (
                    <div className='list-group-item' key={identity.id}>
                        <span>Authentication Request :   ID={identity.idNumber}</span>
                        <ButtonWithProgress className='ml-4' text='See Details' onClick={onClickDetail(identity)} />
                    </div>
                ))}
            </div>
            <Modal visible={modalVisible}
                onClickCancel={onClickCancel}
                onClickOk={onClickVerify}
                onClickDelete={onClickDelete}
                // pendingApiCall={pendingApiCall}
                image={selectedIdentity && selectedIdentity.imageId}
                title='User Verification'
                okButtonText='Verify'
                message={<div>
                    <span>User ID : {selectedIdentity.idNumber}</span>
                </div>}
            />
            {error && <div className='text-center text-danger'> Failed to load requests </div>}

        </div >
    );
};

export default VerifyList;
