import * as ACTIONS from './Constants'
import { login, signup } from '../Api/ApiCalls';

export const logoutSuccess = () => {
    return {
        type: ACTIONS.LOGOUT_SUCCESS
    }
};

export const loginSuccess = (authState) => {
    return {
        type: ACTIONS.LOGIN_SUCCESS,
        payload: authState
    }
}

export const loginHandler = (creds) => {
    return async function (dispatch) {
        const response = await login(creds);  //api process
        console.log("login response :", response);
        const authState = {
            //basic auth
            ...response.data,//username,fullname,image,email
            password: creds.password//response de gelmiyor

        }
        console.log("authstate : ", authState);
        dispatch(loginSuccess(authState));
        return response;
    }
}

export const signupHandler = (user) => {
    return async function (dispatch) {
        console.log(user)

        const response = await signup(user);  //api process

        console.log("sigunp response :");
        console.log(response);

        const newUser = { ...user, username: user.email };
        console.log(newUser)
        await dispatch(loginHandler(newUser))
        
        return response;
    }
}

export const updateSuccess = ({ displayName, image }) => {
    return {
        type: ACTIONS.UPDATE_SUCCESS,
        payload: { displayName, image }
    }
}
