import React from 'react';
var { Timer } = require('lib/easytimer/dist/easytimer.min.js');


const Product = (props) => {
    const { auction } = props;
    const { title, image, startPrice, explanation, endDate, startDate } = auction;

    const firstDate = new Date(startDate);
    const secondDate = new Date(endDate);

    var timer = new Timer();
    timer.start({ countdown: true, startValues: { seconds: 30 } });

    $('#countdownExample .values').html(timer.getTimeValues().toString());

    timer.addEventListener('secondsUpdated', function (e) {
        $('#countdownExample .values').html(timer.getTimeValues().toString());
    });

    timer.addEventListener('targetAchieved', function (e) {
        $('#countdownExample .values').html('KABOOM!!');
    });
    return (
        <div className="col mb-5">
            <div className="card h-100">
                <img className="card-img-top" src={image || "https://dummyimage.com/450x300/dee2e6/6c757d.jpg"} alt="..." />
                <div className="card-body p-5">
                    <div className="text-center">
                        <h5 className="fw-bolder">{title}</h5>
                        <p>{explanation}</p>
                        Price : ${startPrice} <br></br>
                        Remaining Time :
                        <div id="countdownExample">
                            <div className="values"></div>
                        </div>
                    </div>
                </div>
                <div className="card-footer p-4 pt-0 border-top-0 bg-transparent">
                    <div className="text-center">
                        <button className="btn btn-outline-dark mt-auto">View Details</button>
                    </div>
                </div>
            </div>
        </div>

    );
};

export default Product;
