import React,{useEffect, useState} from 'react';
import Input from '../Components/Input';
import ButtonWithProgress from '../Components/ButtonWithProgress';
import { useApiProgress } from '../Shared/ApiProgress';
import {useDispatch} from 'react-redux';
import { loginHandler } from '../Redux/Actions';

const LoginPage = (props) => {

    const [username, setUsername] = useState();
    const [password, setPassword] = useState();
    const [error, setError] = useState();
    const dispatch = useDispatch();

    useEffect(() => {
        setError(undefined)
    }, [username, password])

    const onClickLogin = async (event) => {
        event.preventDefault();

        const creds = { username, password };

        const { history } = props;
        const { push } = history;
        setError(undefined);
        try {
            dispatch(loginHandler(creds));
            //push('/'); 
        } catch (error) {
            console.log(error);
            if(error.response.data.message === "Unauthorized"){
                setError("Username or password is incorrect");
            }else{
                setError(error.response.data.messag)
            }
        }
    }

    
    const buttonEnabled = username && password;
    const pendingApiCall = useApiProgress('post', '/auth',true);

    return (
        <div className='container'>
            <h1 className='text-center'>Login Page</h1>
            <form>
                <Input label='Username'  onChange={event => setUsername(event.target.value)} />
                <Input label='Password' type='password' onChange={event => setPassword(event.target.value)} />
                {error && <div className='alert alert-danger'>{error}</div>}
                <div className='text-center'>
                    <ButtonWithProgress
                        onClick={onClickLogin}
                        disabled={!buttonEnabled || pendingApiCall}
                        pendingApiCall={pendingApiCall}
                        text={'Login'} />
                </div>
            </form>
        </div>
    );
};

export default LoginPage;
