import React from 'react';


const Product = (props) => {
    const {auction}= props;
    const {title,image,startPrice,explanation,endDate,startDate}= auction;

    firstDate=new Date(startDate);
    secondDate=new Date(endDate);
    const diffTime = Math.abs(firstDate - secondDate);
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    const diffHours = Math.ceil(diffTime / (1000 * 60 * 60));
    const diffMinutes = Math.ceil(diffTime / (1000 * 60));
    const diffSeconds = Math.ceil(diffTime / (1000));

    return (
        <div className="col mb-5">
            <div className="card h-100">
                <img className="card-img-top" src={image || "https://dummyimage.com/450x300/dee2e6/6c757d.jpg"} alt="..." />
                <div className="card-body p-5">
                    <div className="text-center">
                        <h5 className="fw-bolder">{title}</h5>
                        <p>{explanation}</p>
                        Price : ${startPrice} <br></br>
                        Remaining Time : {diffDays} days {diffHours} hours {diffMinutes} minutes {diffSeconds} seconds
                    </div>
                </div>
                <div className="card-footer p-4 pt-0 border-top-0 bg-transparent">
                    <div className="text-center">
                        <button className="btn btn-outline-dark mt-auto">View Details</button>
                    </div>
                </div>
            </div>
        </div>

    );
};

export default Product;
