import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import ProfileImageWithDefault from './ProfileImageWithDefault';
import Input from './Input';
import { updateUser } from '../Api/ApiCalls';
import ButtonWithProgress from './ButtonWithProgress';
import { useApiProgress } from '../Shared/ApiProgress';

const ProfileCard = props => {
  const [inEditMode, setInEditMode] = useState(false);
  const [updatedName, setUpdatedName] = useState();
  const [user, setUser] = useState({});

  useEffect(() => {
    setUser(props.user);
  }, [props.user]);

  const { username, name, image } = user;

  useEffect(() => {
    if (!inEditMode) {
      setUpdatedName(undefined);
    } else {
      setUpdatedName(name);
    }
  }, [inEditMode, name]);

  const onClickSave = async () => {
    const body = {
      displayName: updatedDisplayName
    };
    try {
      const response = await updateUser(username, body);
      setInEditMode(false);
      setUser(response.data);
    } catch (error) { }
  };

  const pendingApiCall = useApiProgress('put', '/api/1.0/users/' + username);


  return (
    <div className="card text-center">
      <div className="card-header">
        <ProfileImageWithDefault className="rounded-circle shadow" width="200" height="200" alt={`${username} profile`} image={image} />
      </div>
      <div className="card-body">
        {!inEditMode && (
          <>
            <h3>
              {name}@{username}
            </h3>
            <button className="btn btn-success d-inline-flex" onClick={() => setInEditMode(true)}>
              <i className="material-icons">edit</i>
              Edit
            </button>
          </>
        )}
        {inEditMode && (
          <div>
            <Input
              label='Change Display Name'
              defaultValue={name}
              onChange={event => {
                setUpdatedName(event.target.value);
              }}
            />
            <div>
              <ButtonWithProgress
                className="btn btn-primary d-inline-flex"
                onClick={onClickSave}
                disabled={pendingApiCall}
                pendingApiCall={pendingApiCall}
                text={
                  <>
                    <i className="material-icons">save</i>
                    Save
                  </>
                }
              />
              <button className="btn btn-light d-inline-flex ml-1" onClick={() => setInEditMode(false)} disabled={pendingApiCall}>
                <i className="material-icons">close</i>
                Cancel
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ProfileCard;
