import React from 'react';
import ButtonWithProgress from './ButtonWithProgress';

const Modal = (props) => {
    const { visible , onClickCancel, message,onClickOk , pendingApiCall ,title,okButtonText ,image, onClickDelete} = props;

    let className = 'modal fade';
    if (visible) {
        className += ' show d-block';
    }

    let html=""
    let imageSource;
    if(image){
        imageSource =  'images/identity/' +image;
        html = <img src={imageSource} alt="Identity" className="img-fluid" />
    }
    let htmlDelete=""
    if(onClickDelete){
        htmlDelete = <ButtonWithProgress text='Delete' onClick={onClickDelete} />
    }


    return (
        <div className={className} style={{ backgroundColor: '#000000b0' }}>
            <div className="modal-dialog modal-dialog-centered">
                <div className="modal-content">
                    <div className="modal-header">
                        <h5 className="modal-title">{title}</h5>
                    </div>
                    {image && html}
                    <div className="modal-body">{message}</div>
                    <div className="modal-footer">
                        <button className="btn btn-secondary" disabled={pendingApiCall} onClick={onClickCancel}>
                           Cancel
                        </button>
                        <ButtonWithProgress
                            className="btn btn-danger"
                            onClick={onClickOk}
                            pendingApiCall={pendingApiCall}
                            disabled={pendingApiCall}
                            text={okButtonText}
                        />
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Modal;
